mapper.select:
select * from t_activity


        and activityName like concat('%' ,#{activityName} , '%')


        and activityStatus = #{activityStatus}

SELECT a.createTime, a.rightNum, c.realName, d.orgName, a.openID, d.orgID FROM t_activity_user a, t_user b, t_jobnum c, t_organization d WHERE a.openID = b.openID AND b.jobNum = c.jobNum AND c.orgID = d.orgID and a.activityID = #{activityID}
and c.orgID = #{orgID}
and c.realName like CONCAT("%" ,#{realName} , "%")
